function Cheats(t, a, e) {
    for (var i = $(t).children(".chart_bar"), n = 0; n < i.length; n++) {
        var r = parseInt($(i[n]).next(".label").text()); // Menggunakan .label untuk memilih elemen label
        var widthPercentage = (r / 1) * 10; // Menggunakan r / 1 * 10 sebagai faktor skala
        var maxWidthPercentage = 80; // Batas maksimum lebar dalam persentase
        if (widthPercentage > maxWidthPercentage) {
            widthPercentage = maxWidthPercentage;
        }
        $(i[n]).width(0).delay(a).animate({
            width: widthPercentage + "%"
        }, {
            duration: e
        });
    }
}

function ChangeCSS(t, a, e) {
    $(t + " br").css({ clear: "both" });
    var i = 0.16 * $(a).height(),
        n = 0.16 * $(e).height();
    $(e).css({
        float: "right",
        width: "10%",
        margin: "0 auto",
        "padding-top": n,
        marginTop: "0"
    });
    $(a).css({
        float: "left",
        "text-align": "left",
        marginTop: "0",
        "padding-left": ".3em",
        "margin-bottom": ".3em",
        "white-space": "nowrap",
        "padding-top": i
    });
}

function SkillProgress(t, a, e, i, n) {
    ChangeCSS(t, a, e);
    Cheats(t, i, n);
}
